import React, {Component} from 'react';
import util from '../util';

class Product extends Component{
    constructor(props){
        super(props);
        this.state = {products: [], filteredProducts: []};
      }
      componentWillMount(){
        fetch("http://localhost:3001/products").then(res => res.json())
        .then(data => this.setState({
          products: data,
          filteredProducts: data
        }));
      }
    render(){
        const productItems = this.state.products.map(product => (
            <div className="col-md-4">
                <div className="product-image-wrapper">
                    <div className="single-products">
                        <div className="productinfo text-center">
                            <a
                                href={`#${product.id}`}
                                onClick={this.state.handleAddToCart}>
                            <img src={`/products/${product.img}`} />
                            <h2>{util.formatCurrency(product.price)}</h2>
                            <p> {product.productName} </p>
                            </a>
                            <div>
                            <button className="btn btn-primary"
                                onClick={(e) => this.state.handleAddToCart(e, product)}>Add to Cart</button>
                            </div><br></br>
                        </div>
                    </div>
                </div>
            </div>
        ))
        return(
            <div>
            <section>
                <div className="container">
                <div className="row">
                    <div className="col-sm-3">
                    {/* <div className="left-sidebar">
                        <div className="brands_products"> 
                        <h2>Brands</h2>
                        <div className="brands-name">
                            <ul className="nav nav-pills nav-stacked">
                            <li><a href> <span className="pull-right">(3)</span>SAMSUNG</a></li>
                            <li><a href> <span className="pull-right">(1)</span>POLYTRON</a></li>
                            <li><a href> <span className="pull-right">(3)</span>SHARP</a></li>
                            <li><a href> <span className="pull-right">(2)</span>LG</a></li>
                            <li><a href> <span className="pull-right">(3)</span>TOSHIBA</a></li>
                            </ul>
                        </div>
                        </div> 
                    </div> */}
                    </div>
                    {/* padding-right */}
                    <div className="col-sm-12 ">
                    <div className="features_items">
                        <h2 className="title text-center">Features Items</h2>
                        {productItems}
                        {/* <ul className="pagination">
                        <li className="active"><a href>1</a></li>
                        <li><a href>2</a></li>
                        <li><a href>3</a></li>
                        <li><a href>Â»</a></li>
                        </ul> */}
                    </div>
                    </div>
                </div>
                </div>
            </section>
            </div>


        )
    }
} export default Product
